import React, { Component } from "react";
import firebase from "../firebase/firebase";
import "../static/style.css";
import { Link } from "react-router-dom";

export default class Register extends Component {
  constructor() {
    super();
    this.state = {
      displayName: "",
      email: "",
      password: "",
    };

    this.registerUser = this.registerUser.bind(this);
  }

  handleEmailChange(e) {
    this.setState({ email: e.target.value });
  }
  handlePasswordChange(e) {
    this.setState({ password: e.target.value });
  }
  handledisplaynameChange(e) {
    this.setState({ displayName: e.target.value });
  }
  signup_google = () => {
    const googleAuth = new firebase.auth.GoogleAuthProvider();

    firebase
      .auth()
      .signInWithPopup(googleAuth)
      .then((result) => {
        let user = result.user;

        this.props.history.push("/login");
      })
      .catch((error) => {
        window.alert(error.message);
      });
  };
  registerUser = () => {
    console.log("register user button pressed");

    if (this.state.email === "") {
      alert("Enter email");
      window.location = "/Signup";
    } else {
      if (this.state.password === "") {
        alert("Enter password");
        window.location = "/Signup";
      } else {
        if (this.state.displayName === "") {
          alert("Enter name");
          window.location = "/Signup";
        } else {
          console.log(this.state.email);
          firebase
            .auth()
            .createUserWithEmailAndPassword(
              this.state.email,
              this.state.password
            )
            .catch(function (error) {
              var errorCode = error.code;
              var errorMessage = error.message;
              alert(errorMessage);
              window.location = "/Signup";
            });
          window.alert("Verify link from email!");
          this.props.history.push("/verificationReq");
          console.log("User registered successfully!");
        }
      }
    }
  };
  render() {
    return (
      <div className="container">
        <div className="layer" id="signup">
          <div className="loginArea register" id="signupId">
            <div className="vector"></div>
            <h3 className="loginText">Sign Up</h3>
            <div className="textArea">
              <form>
                <div>
                  <input
                    type="text"
                    placeholder="Enter Name"
                    name="displayName"
                    size="35"
                    required
                    value={this.state.displayName}
                    onChange={this.handledisplaynameChange.bind(this)}
                  ></input>
                </div>
                <div>
                  <input
                    type="text"
                    placeholder="Enter Email"
                    name="email"
                    size="35"
                    required
                    value={this.state.email}
                    onChange={this.handleEmailChange.bind(this)}
                  ></input>
                </div>
                <div>
                  <input
                    type="Password"
                    placeholder="Enter Password"
                    name="password"
                    size="35"
                    value={this.state.password}
                    onChange={this.handlePasswordChange.bind(this)}
                    maxLength={15}
                  />
                </div>
                <div className="btn">
                  <button
                    type="submit"
                    className="submitButton"
                    onClick={this.registerUser}
                  >
                    <b>SIGN UP</b>
                  </button>

                  <Link to="/login" />
                </div>
              </form>
              <div class="social-login">
                <button
                  class="google-btn"
                  onClick={this.signup_google.bind(this)}
                >
                  <img
                    style={{ width: "16px" }}
                    alt="Google"
                    src="data:image/svg+xml;base64, "
                  />
                  <p class="btn-text">Sign up with Google</p>
                </button>
              </div>

              <div className="createAccount">
                <p>Already have an account?&nbsp;&nbsp;</p>
                <Link to="/login" style={{ fontWeight: "600" }}>
                  Sign In
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
